import React, {useState} from 'react'
import { Button, Card, Image, Icon } from 'semantic-ui-react'

function ProjectCard() {
  const [projectDetails, setProjectDetails] = useState({
    project: [
      {
      projectName: "I'm Bored!",
      description: "Random movie, book, and video game generator.",
      repoLink: "https://github.com/Mirandor/team-sith",
      liveLink: "https://mirandor.github.io/team-sith/",
      imageLink: "/Images/ImBored.png"
    },
    {
      projectName: "Password Generator",
      description: "Create simple passwords.",
      repoLink: "https://github.com/Mirandor/pwdgen",
      liveLink: "https://mirandor.github.io/team-sith/",
      imageLink: "/Images/PasswordGenerator.png",
    }
   ]
    // projectName: [
    //   "I'm Bored!",
    //   "Password Generator",
    //   "Burger Love",
    //   "Workday Scheduler",
    //   "Episode Two",
    //   "Note Taker",
    // ],
    // description: [
    //   "Random movie, book, and video game generator.",
    //   "Create simple passwords.",
    //   "Keep track of burgers you want to eat.",
    //   "Plan your day.",
    //   "Stay in a galaxy far far away.",
    //   "A place to take quick simple notes."
    // ],
    // repoLink: [
    //   "https://github.com/Mirandor/team-sith",
    //   "https://github.com/Mirandor/pwdgen",
    //   "https://github.com/Mirandor/burgerlove",
    //   "https://github.com/Mirandor/workdayscheduler",
    //   "https://github.com/Mirandor/Episode-two",
    //   "https://github.com/Mirandor/notetaker"
    // ],
    // liveLink: [
    //   "https://mirandor.github.io/team-sith/",
    //   "https://mirandor.github.io/pwdgen/",
    //   "https://rocky-sierra-19969.herokuapp.com/",
    //   "https://mirandor.github.io/workdayscheduler/",
    //   "https://damp-badlands-10024.herokuapp.com/",
    //   "https://notetaker-darthmirandor.herokuapp.com/"
    // ],
    // imageLink: [
    //   "/Images/ImBored.png",
    //   "/Images/PasswordGenerator.png",
    //   "/Images/ForTheLoveOfBurgers.png",
    //   "/Images/WorkDayScheduler.png",
    //   "/Images/EpisodeTwo.png",
    //   "/Images/NoteTaker.png"
    // ]
  })
  console.log(projectDetails)
  return (
    <div>
      {projectDetails.project.map ( project =>{
        return(
          <Card>
              <Image src={project.imageLink} wrapped ui={false} />
              
        <Card.Content>
            <Card.Header >
              {project.projectName}
            </Card.Header>

          <Card.Description>
            {project.description}
          </Card.Description>
        </Card.Content>
        <Card.Content extra>
          <div className='ui two buttons'>
              <Button basic color='black' href={project.repoLink}>
                Repo
              </Button>
              <Button basic color='black' href={project.liveLink}>
                Live
              </Button>
            </div>
        </Card.Content>
      </Card>
        )
      }
        )}
      
    </div>
    )
}

export default ProjectCard;
